VALUES (3,'Compras \ Fornecedores','Select C.ID As Chave,C.Codigo As Codigo,C.Nome As Descricao from tbFornecedores As C Where ISNULL(C.Esquecido,0) = 0 AND ISNULL(C.Ativo,1) = 1 Order By Codigo ASC'
,1,1,GETDATE(),'F3M',NULL,NULL
,'Select C.Nome As [Nome],C.Abreviatura As [Abreviatura],C.Apelido As [Apelido],C.CartaoCidadao As [CC],cast(convert(nvarchar,C.DataValidade,105) as nvarchar(10)) As [Data_Validade_CC],cast(convert(nvarchar,C.DataNascimento,105) as nvarchar(10)) As [Data_Nasc__],P.Codigo + '' '' + P.Descricao As [Profissão],C.TituloAcademico As [Título_Académico],C.NIB As [IBAN],C.NContribuinte As [Contribuinte],CC.Contato as [Contato],CC.Telefone As [Telefone],CC.Telemovel As [Telemóvel],CC.Fax As [Fax],CC.Email As [E___Mail],CC.PagWeb As [Pág__Web],CC.PagRedeSocial As [Pág__Rede_Social],CM.Descricao AS [Descrição_Morada],CM.Rota As [Rota],CM.Morada As [Morada],CP.Codigo + '' '' + CP.Descricao As [Código___Postal],Con.Codigo + '' - '' + Con.Descricao As [Concelho],D.Codigo + '' - '' + D.Descricao As [Distrito],PA.Descricao As [País],CM.GPS As [GPS] From tbFornecedores As C LEFT JOIN tbProfissoes As P ON P.ID = C.IDProfissao LEFT JOIN tbFornecedoresContatos As CC ON CC.IDFornecedor = C.ID And Ordem = 1 LEFT JOIN tbFornecedoresMoradas As CM ON CM.IDFornecedor = C.ID And CM.Ordem = 1 LEFT JOIN tbCodigosPostais As CP ON CP.ID = CM.IdCodigoPostal LEFT JOIN tbConcelhos As Con ON Con.ID = CM.IDConcelho LEFT JOIN tbDistritos As D ON D.ID = CM.IDDistrito LEFT JOIN tbPaises As PA ON PA.ID = CM.IDPais Where C.ID = <Chave> UNION ALL Select '''' As [Nome],'''' As [Abreviatura],'''' As [Apelido],'''' As [CC],'''' As [Data_Validade_CC],'''' As [Data_Nasc__],'''' As [Profissão],'''' As [Título_Académico],'''' As [IBAN],'''' As [Contribuinte],CC.Contato as [Contato],CC.Telefone As [Telefone],CC.Telemovel As [Telemóvel],CC.Fax As [Fax],CC.Email As [E___Mail],CC.PagWeb As [Pág__Web],CC.PagRedeSocial As [Pág__Rede_Social],'''' AS [Descrição_Morada],'''' As [Rota],'''' As [Morada],'''' As [Código___Postal],'''' As [Concelho],'''' As [Distrito],'''' As [País],'''' As [GPS] From tbFornecedores As C LEFT JOIN tbFornecedoresContatos As CC ON CC.IDFornecedor = C.ID And Ordem = 1 Where C.ID = <Chave> UNION ALL Select '''' As [Nome],'''' As [Abreviatura],'''' As [Apelido],'''' As [CC],'''' As [Data_Validade_CC],'''' As [Data_Nasc__],'''' As [Profissão],'''' As [Título_Académico],'''' As [IBAN],'''' As [Contribuinte],'''' as [Contato],'''' As [Telefone],'''' As [Telemóvel],'''' As [Fax],'''' As [E___Mail],'''' As [Pág__Web],'''' As [Pág__Rede_Social],CM.Descricao AS [Descrição_Morada],CM.Rota As [Rota],CM.Morada As [Morada],CP.Codigo + '' '' + CP.Descricao As [Código___Postal],Con.Codigo + '' - '' + Con.Descricao As [Concelho],D.Codigo + '' - '' + D.Descricao As [Distrito],PA.Descricao As [País],CM.GPS As [GPS] From tbFornecedores As C LEFT JOIN tbFornecedoresMoradas As CM ON CM.IDFornecedor = C.ID And CM.Ordem = 1 LEFT JOIN tbCodigosPostais As CP ON CP.ID = CM.IdCodigoPostal LEFT JOIN tbConcelhos As Con ON Con.ID = CM.IDConcelho LEFT JOIN tbDistritos As D ON D.ID = CM.IDDistrito LEFT JOIN tbPaises As PA ON PA.ID = CM.IDPais Where C.ID = <Chave>'
,NULL,'Select C.ID As Chave,C.Codigo As Codigo,C.Nome As Descricao from tbFornecedores As C Order By Codigo ASC'
,'')

